$_HOAM_language ['help_popups']['config']['user']['flags']['log'] = '<h1>Log Review Flag</h1>' . $_HOAM_language ['help_pages']['generic']['no_changes'] . '<p>This flag controls whether to enable a review of notable system events / changes since the user\'s last login.</p>';
